        //Crear la cita

        //Reiniciar el Form una vez enviado




    } 

    return(
        <Fragment>
            <h2>Crear Cita</h2>
            <form
                onSubmit={submitCita}
            >
                <h4>Datos del Paciente</h4>
                <label>Nombres</label>
                <input
                    type="text"
                    name="nom_paciente"
                    className="u-full-width"
                    placeholder="Nombres"
                    onChange={actualizarState}
                    value={nom_paciente}
                />
                <label>Apellidos</label>
                <input
                    type="text"
                    name="ape_paciente"
                    className="u-full-width"
                    placeholder="Apellidos"
                    onChange={actualizarState}
                    value={ape_paciente}
                />
                <label>Documento de Identificación</label>
                <input
                    type="text"
                    name="dpi_paciente"
                    className="u-full-width"
                    placeholder="DPI"
                    onChange={actualizarState}
                    value={dpi_paciente}
                />
                <h4>Datos de Ingreso</h4>
                <label>Fecha de Ingreso</label>
                <input
                    type="date"
                    name="fecha"
                    className="u-full-width"
                    onChange={actualizarState}
                    value={fecha}
                />
                <label>Hora de Ingreso</label>
                <input
                    type="time"
                    name="hora"
                    className="u-full-width"
                    onChange={actualizarState}
                    value={hora}
                />
                <label>Síntomas</label>
                <textarea
                    className="u-full-width"
                    name="sintomas"
                    onChange={actualizarState}
                    value={sintomas}
                ></textarea>

                <button
                    type="submit"
                    className="u-full-width button-primary"
                >Agregar Cita</button>


            </form>



        </Fragment>
    );
}


export default Formulario;
